package ifeleseif_cs;

public class ifelseif_cs {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=0;
		if (x>0) {
			System.out.println("X is Positive");
		}
		else if (x<0) {
			System.out.println("X is Negetive");
		}
		else {
			System.out.println("X is 0");
		}
	}

}
